"use strict";(self.webpackChunkwebsite=self.webpackChunkwebsite||[]).push([[849],{6164:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"1.1","banner":null,"badge":true,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"docs":[{"type":"link","label":"Intro","href":"/docs/intro","docId":"intro","unlisted":false},{"type":"category","label":"Usage","items":[{"type":"link","label":"Getting Started","href":"/docs/usage/config","docId":"usage/config","unlisted":false},{"type":"link","label":"Common Objects","href":"/docs/usage/objects","docId":"usage/objects","unlisted":false},{"type":"link","label":"Methods","href":"/docs/usage/methods","docId":"usage/methods","unlisted":false},{"type":"link","label":"Callbacks","href":"/docs/usage/callbacks","docId":"usage/callbacks","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Themes","items":[{"type":"link","label":"Themes","href":"/docs/themes/","docId":"themes/index","unlisted":false},{"type":"link","label":"Default","href":"/docs/themes/default","docId":"themes/default","unlisted":false},{"type":"link","label":"Default - Extras","href":"/docs/themes/defaultExtras","docId":"themes/defaultExtras","unlisted":false},{"type":"link","label":"Blue Green Metal","href":"/docs/themes/blueGreenMetal","docId":"themes/blueGreenMetal","unlisted":false},{"type":"link","label":"Dice of Rolling","href":"/docs/themes/diceOfRolling","docId":"themes/diceOfRolling","unlisted":false},{"type":"link","label":"Dice of Rolling - Fate","href":"/docs/themes/diceOfRollingFate","docId":"themes/diceOfRollingFate","unlisted":false},{"type":"link","label":"Gemstone","href":"/docs/themes/gemstone","docId":"themes/gemstone","unlisted":false},{"type":"link","label":"Gemstone Marble","href":"/docs/themes/gemstoneMarble","docId":"themes/gemstoneMarble","unlisted":false},{"type":"link","label":"Genesys","href":"/docs/themes/genesys","docId":"themes/genesys","unlisted":false},{"type":"link","label":"Rock","href":"/docs/themes/rock","docId":"themes/rock","unlisted":false},{"type":"link","label":"Rust","href":"/docs/themes/rust","docId":"themes/rust","unlisted":false},{"type":"link","label":"Smooth","href":"/docs/themes/smooth","docId":"themes/smooth","unlisted":false},{"type":"link","label":"Smooth - Pip","href":"/docs/themes/smoothPip","docId":"themes/smoothPip","unlisted":false},{"type":"link","label":"Wooden","href":"/docs/themes/wooden","docId":"themes/wooden","unlisted":false}],"collapsed":true,"collapsible":true},{"type":"category","label":"Addons","items":[{"type":"link","label":"About Addons","href":"/docs/addons/","docId":"addons/index","unlisted":false},{"type":"link","label":"Dice Parser Interface","href":"/docs/addons/parser","docId":"addons/parser","unlisted":false},{"type":"link","label":"Advanced Roller","href":"/docs/addons/advRoller","docId":"addons/advRoller","unlisted":false},{"type":"link","label":"Dice Picker","href":"/docs/addons/dicePicker","docId":"addons/dicePicker","unlisted":false},{"type":"link","label":"Display Results","href":"/docs/addons/displayResults","docId":"addons/displayResults","unlisted":false},{"type":"link","label":"Box Controls","href":"/docs/addons/boxControls","docId":"addons/boxControls","unlisted":false},{"type":"link","label":"Genesys Dice Picker","href":"/docs/addons/genesysDicePicker","docId":"addons/genesysDicePicker","unlisted":false},{"type":"link","label":"Genesys Results","href":"/docs/addons/genesysResults","docId":"addons/genesysResults","unlisted":false}],"collapsed":true,"collapsible":true}]},"docs":{"addons/advRoller":{"id":"addons/advRoller","title":"Advanced Roller","description":"The Advanced Roller module is a part of the @3d-dice/dice-ui package. This UI module provides a simple text input field and a clear button. The field is connect to Dice Parser Interface. On submit, the field will send the roll notation to FDP to be parsed and will return the result to the callback onSubmit. The clear button will clear out any values stored in the parser and invokes the onClear callback, which is usually a good place to clear your dice-box as well.","sidebar":"docs"},"addons/boxControls":{"id":"addons/boxControls","title":"Box Controls","description":"Box Controls uses the popular dat.GUI module which is self described as:","sidebar":"docs"},"addons/dicePicker":{"id":"addons/dicePicker","title":"Dice Picker","description":"This is a simple UI module aimed at making picking dice simple and easy. I put this module together mostly to make setting up simple rolls on mobile devices easy. Just tap away at the dice you want to roll. It\'s very similar in nature to Advanced Roller.","sidebar":"docs"},"addons/displayResults":{"id":"addons/displayResults","title":"Display Results","description":"Another module available as part of the @3d-dice/dice-ui package. This module takes the final result object as input and creates a modal popup window displaying the final roll results and the final value of that roll.","sidebar":"docs"},"addons/genesysDicePicker":{"id":"addons/genesysDicePicker","title":"Genesys Dice Picker","description":"This module has all the same features as Dice Picker. There are only a few UI differences to accommodate this unique dice set. Just tap away at the dice you want to roll.","sidebar":"docs"},"addons/genesysResults":{"id":"addons/genesysResults","title":"Genesys Results","description":"This module has most of the same features as Display Results. It takes the final result object as input and creates a modal popup window displaying the final roll result totals.","sidebar":"docs"},"addons/index":{"id":"addons/index","title":"About Addons","description":"In this project I refer to addons as JavaScript modules that can be used with Dice-Box. So far I\'ve created four modules that are designed to integrate with Dice-Box. More are planned and I certainly encourage others to build their own.","sidebar":"docs"},"addons/parser":{"id":"addons/parser","title":"Dice Parser Interface","description":"As mentioned in the config setup, Dice-Box requires a parser to do the fun TTRPG things. Any roll notations that are more than the simple pattern d{side}+/-{modifier} have to go through a parser to make sense of the notation. All the rolls supported are documented at Roll20 Dice Specification","sidebar":"docs"},"intro":{"id":"intro","title":"Intro","description":"Do you love TTRPG and wish you had some 3D dice to incorporate into your side projects? That\'s exactly why I started this project. This is a high performance 3D dice roller made with BabylonJS, AmmoJS and implemented with lightning fast web workers and offscreenCanvas. Designed to be easy to integrate into your own JavaScript app with a package payload under 1 megabyte after compression.","sidebar":"docs"},"themes/blueGreenMetal":{"id":"themes/blueGreenMetal","title":"Blue Green Metal","description":"A rough metallic theme using standard textures. This theme uses the smoothDice mesh.","sidebar":"docs"},"themes/default":{"id":"themes/default","title":"Default","description":"The default theme that is packaged with Dice Box. These are sharp edged numerical dice. The mesh name is default.","sidebar":"docs"},"themes/defaultExtras":{"id":"themes/defaultExtras","title":"Default - Extras","description":"This theme extends the Default theme, adding a two sided coin, a fate die and a d6 with pips. In order to use it with the default theme, simply load it using the new preloadThemes config option.","sidebar":"docs"},"themes/diceOfRolling":{"id":"themes/diceOfRolling","title":"Dice of Rolling","description":"A theme inspired by the original Dice of Rolling, which is one of my favorite dice sets. This theme uses the smoothDice mesh.","sidebar":"docs"},"themes/diceOfRollingFate":{"id":"themes/diceOfRollingFate","title":"Dice of Rolling - Fate","description":"A theme extending the Dice of Rolling theme with an orange fate die.","sidebar":"docs"},"themes/gemstone":{"id":"themes/gemstone","title":"Gemstone","description":"This theme is an attempt to create some uniquely shaped dice that are quite different from the standard set. This theme uses the gemstone mesh.","sidebar":"docs"},"themes/gemstoneMarble":{"id":"themes/gemstoneMarble","title":"Gemstone Marble","description":"This theme slaps an ugly marbled texture on the gemstone dice. This theme was more a proof of concept. This theme uses the gemstone mesh.","sidebar":"docs"},"themes/genesys":{"id":"themes/genesys","title":"Genesys","description":"The first theme made available that uses symbols instead of numbers. This theme posed many challenges for implementation but is finally ready for release with v1.1.0. This theme uses the genesys mesh. A special thanks to Arran France and the team at Quest Portal for providing the original dice set.","sidebar":"docs"},"themes/index":{"id":"themes/index","title":"Themes","description":"Everyone loves different dice colors and styles. Also, sometimes it\'s important to have different colored dice at the virtual table in order to distinguish who\'s making rolls. With that in mind I\'ve tried to keep the theme layer flexible and easy to customize.","sidebar":"docs"},"themes/rock":{"id":"themes/rock","title":"Rock","description":"A rough surface texture meant to appear stone like. This theme uses transparent png files to apply some surface coloring and texture, while allowing for a configurable base color. This theme uses the smoothDice mesh.","sidebar":"docs"},"themes/rust":{"id":"themes/rust","title":"Rust","description":"A rough surface texture meant to appear like weathered and blistered metal. This theme uses transparent png files to apply some surface coloring and texture, while allowing for a configurable base color. This theme uses the smoothDice mesh.","sidebar":"docs"},"themes/smooth":{"id":"themes/smooth","title":"Smooth","description":"For lack of a better name, this is the smooth dice theme with rounded edges and a serif font. This was the original default dice set for v0.6.0 and implemented the original base mesh smoothDice used by many other themes.","sidebar":"docs"},"themes/smoothPip":{"id":"themes/smoothPip","title":"Smooth - Pip","description":"Extends the Smooth theme by adding a six sided dice with pips instead of numbers.","sidebar":"docs"},"themes/wooden":{"id":"themes/wooden","title":"Wooden","description":"A rustic wooden texture with raised wood grain patterns. This theme uses the smoothDice mesh.","sidebar":"docs"},"usage/callbacks":{"id":"usage/callbacks","title":"Callbacks","description":"onDieComplete","sidebar":"docs"},"usage/config":{"id":"usage/config","title":"Getting Started","description":"It should be noted that Dice-Box can only accept simple dice notations or notation objects such as 2d20 or . Once the notation has been sent to Dice-Box using the roll method, it will kick off the 3D physics simulation. When dice have stopped moving, the module will return a detailed result object that you are free to parse as you see fit.","sidebar":"docs"},"usage/methods":{"id":"usage/methods","title":"Methods","description":"Promised based rolls","sidebar":"docs"},"usage/objects":{"id":"usage/objects","title":"Common Objects","description":"Roll Object","sidebar":"docs"},"versions":{"id":"versions","title":"Versions","description":"- 1.0"}}}}')}}]);